    void jarakConverter() {
		double awal = 0;
		

		String jarakAsal = (String) Jarakbox1.getSelectedItem();
		String jarakAkhir = (String) Jarakbox2.getSelectedItem();

                try{
                    awal = Double.parseDouble(Jaraktxt.getText());
                    // Double.parseDouble(jarakAkhir);
                }catch(NumberFormatException e){
                    return;
                }
                
		Double hasil = Jarak.convert(jarakAsal, jarakAkhir) * awal;
		Jarakhasil.setText(String.valueOf(hasil));
	}
