    private void waktuConvert() {
		double awal = Double.parseDouble(Waktutxt.getText());

		String waktuAwal = (String) Waktubox1.getSelectedItem();
		String waktuAkhir = (String) Waktubox2.getSelectedItem();
                
//                if(!waktuAwal.matches("[0-9]+")){
//                    return;
//                }
//                
//                if(!waktuAkhir.matches("[0-9]+",".")){
//                    return;
//                }

		double hasil = Waktu.convert(waktuAwal, waktuAkhir) * awal;
		Waktuhasil.setText(String.valueOf(hasil));
	}
    private void SuhutxtKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_SuhutxtKeyReleased
        tempratureConvert();
    }//GEN-LAST:event_SuhutxtKeyReleased

    private void tempratureConvert(){
        String suhuAsal = (String) Suhubox1.getSelectedItem();
        String suhuAkhir = (String) Suhubox2.getSelectedItem();

        double nilai = Double.parseDouble(Suhutxt.getText());

        double hasil = Suhu.coverter(suhuAsal, suhuAkhir, nilai);

        Suhuhasil.setText(String.valueOf(hasil));
    }
